"use strict";var chai=require("chai"),expect=chai.expect,IntlMessageFormat=require("../");describe("no locale",function(){describe("no locale provided",function(){it("should default to English",function(){var e=new IntlMessageFormat("I have {NUM_BOOKS, plural, =1 {1 book} other {# books}}.");expect(e.resolvedOptions().locale).to.equal("en"),expect(e.format({NUM_BOOKS:2})).to.equal("I have 2 books.")})}),describe("invalid locale default",function(){it("should fallback to default locale",function(){IntlMessageFormat.__addLocaleData({locale:"fu-baz",pluralRuleFunction:function(e){return null}});var e=new IntlMessageFormat("{COMPANY_COUNT, plural, =1 {One company} other {# companies}} published new books."),o=e.format({COMPANY_COUNT:1});expect(o).to.equal("One company published new books.")})})});